import "bootstrap/dist/css/bootstrap.css";
import "bootstrap-daterangepicker/daterangepicker.css";
import React, { useState, useEffect } from "react";
import { Card, Form, Button, Col } from "react-bootstrap";
import { useHistory, useParams } from "react-router-dom";
import axios from "axios";
import ToastComponent from "./ToastComponent";
import DateRangePicker from "react-bootstrap-daterangepicker";
import moment from "react-moment";

const values = {
  id: "",
  projectName: "",
  cost: "",
  time: "",
};

function AddProject() {
  const [form, setForm] = useState(values);
  const [show, setShow] = useState(false);
  const [method, setMethod] = useState("");

  let history = useHistory();
  let { id } = useParams();

  const [fromDate, setFromDate] = useState(new Date());
  const [toDate, setToDate] = useState(new Date());
  const range = {
    Today: [moment(), moment()],
    Yesterday: [moment().subtract(1, "days"), moment().subtract(1, "days")],
    "Last 7 Days": [moment().subtract(6, "days"), moment()],
    "Last 30 Days": [moment().subtract(29, "days"), moment()],
    "This Month": [moment().startOf("month"), moment().endOf("month")],
    "Last Month": [
      moment().subtract(1, "month").startOf("month"),
      moment().subtract(1, "month").endOf("month"),
    ],
    "Last Year": [
      moment().subtract(1, "year").startOf("year"),
      moment().subtract(1, "year").endOf("year"),
    ],
  };

  const handleEvent = (event, picker) => {
    console.log("start: ", picker.startDate._d);
    console.log("end: ", picker.endDate._d);
    setFromDate(picker.startDate._d.toISOString());
    setToDate(picker.endDate._d.toISOString());
  };

  /** ---------------------- controller ---------------------- */

  // Project data save
  const submitProject = async (e) => {
    e.preventDefault();

    await axios
      .post("http://localhost:8080/project/add", JSON.stringify(form), {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((res) => {
        if (res.data != null) {
          setShow(true);
          setMethod("post");
          setTimeout(() => setShow(false), 1500);
        } else {
          setShow(false);
        }
      });
  };

  /** update */
  // Project data update
  const updateProject = async (e) => {
    e.preventDefault();

    await axios
      .put(`http://localhost:8080/project/put/${id}`, JSON.stringify(form), {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((res) => {
        if (res.data != null) {
          setShow(true);
          setMethod("put");
          setTimeout(() => setShow(false), 1500);
          setTimeout(() => projectList(true), 1500);
        } else {
          setShow(false);
        }
      });
  };

  // find project with id to show when we want to update
  const findProjectById = async (id) => {
    await axios
      .get(`http://localhost:8080/project/get/${id}`)
      .then((res) => {
        if (res.data != null) {
          setForm({
            id: res.data.id,
            projectName: res.data.projectName,
            cost: res.data.cost,
            time: res.data.time,
          });
        }
      })
      .catch((error) => {
        console.error(error);
      });
  };

  /** update end*/

  /** ---------------------- controller end ---------------------- */

  // to add in project list
  const projectChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // to reset project values that we wrote
  const resetProject = () => {
    setForm(values);
  };

  // back to project list
  const projectList = () => {
    history.push("/list-project");
  };

  // for memo
  useEffect(() => {
    findProjectById(id);
  }, [id]);

  return (
    <div>
      <div style={{ display: show ? "block" : "none" }}>
        <ToastComponent
          children={{
            show: show,
            message: method === "post" ? "Project Saved!" : "Project Updated!",
            type: "success",
          }}
        />
      </div>

      <Card
        className={"border border-dark bg-dark text-white"}
        style={{ margin: "45px 0px" }}
      >
        <Card.Header>{id ? "Update Project" : "Save Project"}</Card.Header>

        <Form
          id="projectFormId"
          onSubmit={id ? updateProject : submitProject}
          onReset={resetProject}
        >
          <Card.Body>
            <Form.Group as={Col}>
              <Form.Label>Project Name</Form.Label>
              <Form.Control
                required
                autoComplete="off"
                type="text"
                name="projectName"
                placeholder="Enter project name"
                className={"bg-dark text-white"}
                onChange={projectChange}
                value={form.projectName}
              />
            </Form.Group>
            <Form.Group as={Col}>
              <Form.Label>Cost</Form.Label>
              <Form.Control
                required
                autoComplete="off"
                type="number"
                step="any"
                name="cost"
                placeholder="Enter project cost"
                className={"bg-dark text-white"}
                onChange={projectChange}
                value={form.cost}
              />
            </Form.Group>
            <Form.Group as={Col}>
              <Form.Label>Time</Form.Label>
              <DateRangePicker
                // startDate={new Date()}
                // endDate={new Date()}
                ranges={range}
                alwaysShowCalendars={true}
                onEvent={handleEvent}
              >
                <button>
                  {moment(fromDate).format("LL")} to{" "}
                  {moment(toDate).format("LL")}
                </button>
              </DateRangePicker>
            </Form.Group>
          </Card.Body>
          <Card.Footer style={{ textAlign: "right" }}>
            <Button
              size="sm"
              type="submit"
              variant="success"
              style={{ margin: "2px" }}
            >
              {id ? "Update" : "Save"}
            </Button>
            <Button
              size="sm"
              type="reset"
              variant="info"
              style={{ margin: "2px" }}
            >
              Reset
            </Button>
            <Button
              size="sm"
              type="button"
              variant="info"
              style={{ margin: "2px" }}
              onClick={projectList}
            >
              Project List
            </Button>
          </Card.Footer>
        </Form>
      </Card>
    </div>
  );
}

export default React.memo(AddProject);
