import "bootstrap/dist/css/bootstrap.css";
import React, { useState, useEffect } from "react";
import { Card, Form, Button, Col } from "react-bootstrap";
import axios from "axios";
import ToastComponent from "./ToastComponent";
import { Doughnut } from "react-chartjs-2";

// get current date
const getCurrentDate = (separator = "-") => {
  let newDate = new Date();
  let date = newDate.getDate();
  let month = newDate.getMonth() + 1;
  let year = newDate.getFullYear();

  return `${year}${separator}${
    month < 10 ? `0${month}` : `${month}`
  }${separator}${date}`;
};

// initalize values
const values = {
  id: "",
  userName: "deneme",
  projectName: "deneme",
  startedTime: getCurrentDate(),
  totalTime: "",
};

function ProgressComponent() {
  const [projects, setProjects] = useState([]);
  const [users, setUsers] = useState([]);
  const [form, setForm] = useState(values);
  const [show, setShow] = useState(false);
  const [method, setMethod] = useState("");

  const data = {
    labels: ["Red", "Blue", "Yellow", "Green", "Purple", "Orange"],
    datasets: [
      {
        label: "# of Votes",
        data: [12, 19, 3, 5, 2, 3],
        backgroundColor: [
          "rgba(255, 99, 132, 0.2)",
          "rgba(54, 162, 235, 0.2)",
          "rgba(255, 206, 86, 0.2)",
          "rgba(75, 192, 192, 0.2)",
          "rgba(153, 102, 255, 0.2)",
          "rgba(255, 159, 64, 0.2)",
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
          "rgba(75, 192, 192, 1)",
          "rgba(153, 102, 255, 1)",
          "rgba(255, 159, 64, 1)",
        ],
        borderWidth: 1,
      },
    ],
  };

  const DoughnutChart = () => (
    <>
      <div className="header">
        <h1 className="title">Doughnut Chart</h1>
        <div className="links">
          <a
            className="btn btn-gh"
            href="https://github.com/reactchartjs/react-chartjs-2/blob/master/example/src/charts/Doughnut.js"
          >
            Github Source
          </a>
        </div>
      </div>
      <Doughnut data={data} />
    </>
  );
  useEffect(() => {
    axios
      .get("http://localhost:8080/project/get/")
      .then((res) => setProjects(res.data));
    axios
      .get("http://localhost:8080/admin/get/")
      .then((res) => setUsers(res.data));
  }, []);

  const projectChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });

    // const index = e.target.selectedIndex;
    // const el = e.target.childNodes[index];
    // const option = el.getAttribute("id");
  };

  const submitProgress = async (e) => {
    e.preventDefault();

    await axios
      .post("http://localhost:8080/progress/add", JSON.stringify(form), {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((res) => {
        if (res.data != null) {
          setShow(true);
          setMethod("post");
          setTimeout(() => setShow(false), 1500);
        } else {
          setShow(false);
        }
      });
  };

  return (
    <div>
      <div style={{ display: show ? "block" : "none" }}>
        <ToastComponent
          children={{
            show: show,
            message:
              method === "post" ? "Progress Saved!" : "Progress Updated!",
            type: "success",
          }}
        />
      </div>
      <Card
        className={"border border-dark bg-dark text-white"}
        style={{ margin: "45px 0px" }}
      >
        <Card.Header>{"Save Progress"}</Card.Header>

        <Form id="progressFormId" onSubmit={submitProgress}>
          <Card.Body>
            <Form.Group as={Col} controlId="dateId">
              <Form.Label>Date</Form.Label>
              <Form.Control
                required
                type="date"
                name="startedTime"
                placeholder="Date of Start"
                onChange={projectChange}
                value={form.startedTime}
                className={"bg-dark text-white"}
              />
            </Form.Group>

            <Form.Group as={Col}>
              <Form.Label>Select Project</Form.Label>
              <Form.Select
                className={"bg-dark text-white"}
                aria-label="select project"
                name="projectName"
                value={form.projectName}
                onChange={projectChange}
              >
                {projects.map((project, index) => (
                  <option key={project.id || index} id={project.id}>
                    {project.projectName}
                  </option>
                ))}
              </Form.Select>
            </Form.Group>
            <Form.Group as={Col}>
              <Form.Label>Select User</Form.Label>
              <Form.Select
                className={"bg-dark text-white"}
                aria-label="select user"
                value={form.userName}
                name="userName"
                onChange={projectChange}
              >
                {users.map((user, index) => (
                  <option key={user.id || index}>{user.userName}</option>
                ))}
              </Form.Select>
            </Form.Group>
          </Card.Body>

          <Card.Footer style={{ textAlign: "right" }}>
            <Button
              size="sm"
              type="submit"
              variant="success"
              style={{ margin: "2px" }}
            >
              Save
            </Button>

            <Button
              size="sm"
              type="button"
              variant="info"
              style={{ margin: "2px" }}
            >
              Project List
            </Button>
          </Card.Footer>
        </Form>
      </Card>
    </div>
  );
}

export default ProgressComponent;
