function ProgressList() {
  const [chartData, setChartData] = useState({});

  const chart = () => {
    let empSal = [];
    let empAge = [];
    axios
      // .get("http://dummy.restapiexample.com/api/v1/employees")
      .get("http://localhost:8080/progress/get/")
      .then((res) => {
        for (const dataObj of res.data) {
          const cats = dataObj.reduce(
            (catsSoFar, { userName, projectName }) => {
              if (!catsSoFar[userName]) catsSoFar[userName] = [];
              catsSoFar[userName].push(projectName);
              return catsSoFar;
            },
            {}
          );
          console.log(cats);
          empSal.push(dataObj.id);
          empAge.push(dataObj.userName);
          // empSal.push(parseInt(dataObj.userName));
          // empAge.push(dataObj.projectName);
        }
        setChartData({
          labels: empAge,
          datasets: [
            {
              label: "which user which project",
              data: empSal,
              backgroundColor: ["rgba(75, 192, 192, 0.6)"],
              borderWidth: 4,
            },
          ],
        });
      })
      .catch((err) => {
        console.log(err);
      });
    console.log(empSal, empAge);
  };

  useEffect(() => {
    chart();
  }, []);
  return (
    <div className="App">
      <h1>Projects</h1>
      <div>
        <Line
          data={chartData}
          options={{
            responsive: true,
            title: { text: "THICCNESS SCALE", display: true },
            scales: {
              yAxes: [
                {
                  ticks: {
                    autoSkip: true,
                    maxTicksLimit: 10,
                    beginAtZero: true,
                  },
                  gridLines: {
                    display: false,
                  },
                },
              ],
              xAxes: [
                {
                  gridLines: {
                    display: false,
                  },
                },
              ],
            },
          }}
        />
      </div>
    </div>
  );
}

export default ProgressList;
