/* eslint-disable no-lone-blocks */
import { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";

function ProgressList() {
  const [chartData, setChartData] = useState({});
  const [projects, setProjects] = useState([]);

  // grouping data like users have how much projects
  const groupingArr = projects.reduce(
    (projectsSoFar, { userName, projectName }) => {
      if (!projectsSoFar[userName]) projectsSoFar[userName] = [];
      projectsSoFar[userName].push(projectName);
      return projectsSoFar;
    },
    {} //initial valuesss
  );

  useEffect(() => {
    let proje = [];
    let user = [];

    axios
      // .get("http://dummy.restapiexample.com/api/v1/employees")
      .get("http://localhost:8080/progress/get/")
      .then((res) => setProjects(res.data))
      .catch((err) => {
        console.log("error" + err);
      });

    {
      Object.keys(groupingArr).map((item, index) => {
        user.push(item);
        return index;
      });
    }
    {
      Object.values(groupingArr).map((item, index) => {
        proje.push(item.length);
        return index;
      });
    }

    setChartData({
      labels: user,
      datasets: [
        {
          label: "users in",
          data: proje,
          backgroundColor: ["rgba(75, 192, 192, 0.6)"],
          borderWidth: 4,
        },
      ],
    });
  }, []);

  return (
    <div className="App">
      <h1>Projects</h1>
      <div>
        <Line
          data={chartData}
          options={{
            responsive: true,
            title: { text: "THICCNESS SCALE", display: true },
            scales: {
              yAxes: [
                {
                  ticks: {
                    autoSkip: true,
                    maxTicksLimit: 10,
                    beginAtZero: true,
                  },
                  gridLines: {
                    display: false,
                  },
                },
              ],
              xAxes: [
                {
                  gridLines: {
                    display: false,
                  },
                },
              ],
            },
          }}
        />
      </div>
    </div>
  );
}

export default ProgressList;
