import React, { useEffect, useState } from "react";
import {
  InputGroup,
  ButtonGroup,
  Card,
  Table,
  Button,
  FormControl,
  ProgressBar,
} from "react-bootstrap";
import axios from "axios";
import ToastComponent from "./ToastComponent";
import { Link } from "react-router-dom";
import moment from "moment";

import "bootstrap/dist/css/bootstrap.min.css";
const calculateDaysLeft = (startDate, endDate) => {
  if (!moment.isMoment(startDate)) startDate = moment(startDate);
  if (!moment.isMoment(endDate)) endDate = moment(endDate);

  return endDate.diff(startDate, "days");
};

const getCurrentDate = (separator = "-") => {
  let newDate = new Date();
  let date = newDate.getDate();
  let month = newDate.getMonth() + 1;
  let year = newDate.getFullYear();

  return `${year}${separator}${
    month < 10 ? `0${month}` : `${month}`
  }${separator}${date}`;
};

function ProjectList() {
  const [projects, setProjects] = useState([]);
  const [show, setShow] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [projectsPerPage] = useState(5);
  const [searchTerm, setSearchTerm] = useState("");

  // console.log(props);
  const lastIndex = currentPage * projectsPerPage;
  const firstIndex = lastIndex - projectsPerPage;
  const currentProjects = projects.slice(firstIndex, lastIndex);
  const totalPages = Math.ceil(projects.length / projectsPerPage);

  // for get data
  const getProject = async () => {
    await axios("http://localhost:8080/project/get").then((res) =>
      setProjects(res.data)
    );
  };
  useEffect(() => {
    getProject();
  }, [projects]);

  // delete data with spesific id
  const deleteProject = async (projectId) => {
    await axios
      .delete(`http://localhost:8080/project/ ${projectId}`)
      .then((res) => {
        if (res.data != null) {
          setShow(true);
          setTimeout(() => setShow(false), 1500);
        } else {
          setShow(false);
        }
      });
  };

  // Button pagination stuff
  const firstPage = () => {
    if (currentPage > 1) {
      setCurrentPage(1);
    }
  };
  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };
  const lastPage = () => {
    if (currentPage < Math.ceil(projects.length / projectsPerPage)) {
      setCurrentPage(Math.ceil(projects.length / projectsPerPage));
    }
  };
  const nextPage = () => {
    if (currentPage < Math.ceil(projects.length / projectsPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  // formcontrol page number control stuff
  const changePage = (e) => {
    setCurrentPage(e.target.value <= totalPages ? e.target.value : totalPages);
  };

  return (
    <div style={{ heigth: "100px" }}>
      <div style={{ display: show ? "block" : "none" }}>
        <ToastComponent
          children={{ show: show, message: "Project Deleted!", type: "danger" }}
        />
      </div>

      <Card
        className={"border border-dark bg-dark text-white"}
        style={{ margin: "45px 0" }}
      >
        <Card.Header>Project List</Card.Header>
        <Card.Body>
          <Table striped bordered hover variant="dark">
            <thead>
              <tr>
                <th>Project Name</th>
                <th>Start Date</th>
                <th>End Date</th>
                <th> Total Days</th>
                <th>Progress</th>
                <th>Actions</th>
              </tr>
              <tr></tr>
            </thead>
            <tbody>
              {projects.length === 0 ? (
                <tr align="center">
                  <td colSpan="5">No Projects Available..</td>
                </tr>
              ) : (
                currentProjects.map((project) => (
                  <tr key={project.id}>
                    <td>{project.projectName}</td>
                    <td>{project.startDate}</td>
                    <td>{project.endDate}</td>
                    <td>
                      {calculateDaysLeft(project.startDate, project.endDate)}
                    </td>
                    <td>
                      <ProgressBar
                        now={calculateDaysLeft(
                          project.startDate,
                          project.endDate
                        )}
                      />
                    </td>
                    <td>
                      <ButtonGroup>
                        <Link
                          to={"/edit-project/" + project.id}
                          className="btn btn-sm btn-outline-primary"
                        >
                          edit
                        </Link>

                        <Button
                          size="sm"
                          variant="outline-danger"
                          onClick={() => deleteProject(project.id)}
                        >
                          delete
                        </Button>
                      </ButtonGroup>
                      <Form className="d-flex">
                        <FormControl
                          type="search"
                          placeholder="Search"
                          className="me-2"
                          aria-label="Search"
                          onChange={(e) => {
                            setSearchTerm(e.target.value);
                          }}
                        />
                        {projects
                          .filter((project) => {
                            if (searchTerm === "") {
                              return project;
                            } else if (
                              project.projectName
                                .toLowerCase()
                                .includes(searchTerm.toLowerCase())
                            ) {
                              return project;
                            }
                          })
                          .map((pro, index) => {
                            return <div key={pro.id || index}></div>;
                          })}
                        {/* <Button variant="outline-success">Search</Button> */}
                      </Form>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </Table>
        </Card.Body>
        <Card.Footer>
          <div style={{ float: "left" }}>
            {" "}
            {currentPage} of {totalPages}
          </div>
          <div style={{ float: "right" }}>
            <InputGroup size="sm">
              <Button
                type="button"
                variant="outline-info"
                disabled={currentPage === 1 ? true : false}
                onClick={firstPage}
              >
                First
              </Button>
              <Button
                type="button"
                variant="outline-info"
                disabled={currentPage === 1 ? true : false}
                onClick={prevPage}
              >
                Prev
              </Button>

              <FormControl
                style={{
                  textAlign: "center",
                  width: "40px",
                  color: "white",
                }}
                className={"bg-dark"}
                name="currentPage"
                defaultValue={currentPage}
                onChange={changePage}
              />

              <Button
                type="button"
                variant="outline-info"
                disabled={currentPage === totalPages ? true : false}
                onClick={nextPage}
              >
                Next
              </Button>
              <Button
                type="button"
                variant="outline-info"
                disabled={currentPage === totalPages ? true : false}
                onClick={lastPage}
              >
                Last
              </Button>
            </InputGroup>
          </div>
        </Card.Footer>
      </Card>
    </div>
  );
}

export default React.memo(ProjectList);
